		long expoTotal = expoExp*(200-dignity);
		long[] weights = new long[15];
		weights[1] = (hateTotal*2700/(pleaTotal+injuTotal+expoTotal));
		weights[2] = (pleaTotal*2700/(hateTotal+injuTotal+expoTotal));
		weights[3] = (injuTotal*2700/(hateTotal+pleaTotal+expoTotal));
		weights[4] = (expoTotal*2700/(hateTotal+pleaTotal+injuTotal));
