		weights[8] = (pleaTotal+injuTotal)*1000/(hateTotal+expoTotal);
		weights[9] = (pleaTotal+expoTotal)*1000/(hateTotal+injuTotal);
		weights[10] = (injuTotal+expoTotal)*1000/(hateTotal+pleaTotal);
		weights[11] = (hateTotal+pleaTotal+injuTotal)*1000/(expoTotal*3);
		weights[12] = (hateTotal+pleaTotal+expoTotal)*1000/(injuTotal*3);
		weights[13] = (hateTotal+injuTotal+expoTotal)*1000/(pleaTotal*3);
		weights[14] = (pleaTotal+injuTotal+expoTotal)*1000/(hateTotal*3);
