	public int intensity(int baseParameter, int currentParameter, int parameterCount) {
		int total = currentParameter;
		if (baseParameter > 0) {
			total += 5;
			if (total < baseParameter + 5*parameterCount) {
				total = baseParameter + 5*parameterCount;
			}
		}
		return total;
	}
	
