	public int EECost() {
		int value = 1000;
		int magnitude = disgrace/10;
		int remainder = disgrace % 10;
		if (remainder == 0) {
			value = 1000;
		} else if (remainder == 1) {
			value = 933;
		} else if (remainder == 2) {
			value = 871;
		} else if (remainder == 3) {
			value = 812;
		} else if (remainder == 4) {
			value = 758;
		} else if (remainder == 5) {
			value = 707;
		} else if (remainder == 6) {
			value = 660;
		} else if (remainder == 7) {
			value = 616;
		} else if (remainder == 8) {
			value = 574;
		} else if (remainder == 9) {
			value = 536;
		}
		while (magnitude > 0) {
			if (value % 2 == 0) {
				value = value/2;
			} else {
				value = (value/2)+1;
			}
			magnitude--;
		}
		return value;
	}
	
	public int opinion(Forsaken x) {
		int sum = 0;
		if (x.equals(firstPartner)) {
			if (firstOriginalRelationship == 4) {
				sum = 1000;
			} else if (firstOriginalRelationship == 2) {
				sum = 300;
			} else if (firstOriginalRelationship >= 0) {
				sum = 100;
			} else if (firstOriginalRelationship == -2) {
				sum = -300;
			} else {
				sum = -1000;
			}
		} else if (x.equals(secondPartner)) {
			if (secondOriginalRelationship == 4) {
				sum = 1000;
			} else if (secondOriginalRelationship == 2) {
				sum = 300;
			} else if (secondOriginalRelationship >= 0) {
				sum = 100;
			} else if (secondOriginalRelationship == -2) {
				sum = -300;
			} else {
				sum = -1000;
			}
		}
		if (x.kills != null) {
			if (x.kills.length > 0 && x.kills[0] != null) {
				if (firstFormerPartner != null && secondFormerPartner != null) {
					if (x.kills[0].equals(firstFormerPartner)) {
						if (firstOriginalRelationship == 4) {
							sum -= 700;
						} else if (firstOriginalRelationship >= 0) {
							sum -= 500;
						}
					} else if (x.kills[0].equals(secondFormerPartner)) {
						if (secondOriginalRelationship == 4) {
							sum -= 700;
						} else if (secondOriginalRelationship >= 0) {
							sum -= 500;
						}
					}
				}
			}
		}
		sum += 400;
		sum -= hostility;
		sum -= x.disgrace;
		if (deviancy < x.deviancy) {
			sum -= (x.deviancy - deviancy)*2;
		}
		if (x.obedience < obedience) {
			sum -= (obedience - x.obedience)*2;
		}
		if (others != null) {
			for (int i = 0; i < others.length; i++) {
				if (others[i].equals(x)) {
					sum -= troublemaker[i];
				}
			}
		}
		return sum;
	}
	
