    public int opinion(final Forsaken x) {
        int sum = 0;
        for (int i = 0; i < this.formerPartners.length; ++i) {
            if (this.formerPartners[i].equals(x.formerSelf)) {
                if (this.formerFriendships[i] == 4) {
                    sum = 1000;
                }
                else if (this.formerFriendships[i] == 2) {
                    sum = 300;
                }
                else if (this.formerFriendships[i] >= 0) {
                    sum = 100;
                }
                else if (this.formerFriendships[i] == -2) {
                    sum = -300;
                }
                else {
                    sum = -1000;
                }
            }
        }
        for (int i = 0; i < this.formerPartners.length; ++i) {
            for (int j = 0; j < x.kills.length; ++j) {
                if (this.formerPartners[i].equals(x.kills[j])) {
                    if (this.formerFriendships[i] == 4) {
                        sum -= 700;
                    }
                    else if (this.formerFriendships[i] >= 0) {
                        sum -= 500;
                    }
                }
            }
        }
        sum += 400;
        sum -= this.hostility;
        sum -= x.disgrace;
        if (this.deviancy < x.deviancy) {
            sum -= (x.deviancy - this.deviancy) * 2;
        }
        if (x.obedience < this.obedience) {
            sum -= (this.obedience - x.obedience) * 2;
        }
        if (this.others != null) {
            for (int i = 0; i < this.others.length; ++i) {
                if (this.others[i].equals(x)) {
                    sum -= this.troublemaker[i];
                }
            }
        }
        return sum;
    }
    
    public int compatibility(final Chosen c) {
        if (this.knowsPersonally(c)) {
            return 8;
        }
        return (850 - (Math.abs(this.morality - c.morality) + Math.abs(this.innocence - c.innocence) + Math.abs(this.confidence - c.confidence) + Math.abs(this.dignity - c.dignity)) * 4 / 3) / 100;
    }
    
    public long expMultiplier(long exp) {
        long value = 1000L;
        if (exp < 20000L) {
            value = exp / 20L;
        }
        else {
            while (exp > 20000L) {
                exp = exp * 99322L / 100000L;
                ++value;
            }
        }
        return value;
    }
    
    public String expMultiplierDisplay(long expDisplay) {
        expDisplay = this.expMultiplier(expDisplay);
        String displayed = String.valueOf(expDisplay / 1000L) + ".";
        expDisplay %= 1000L;
        if (expDisplay < 100L) {
            displayed = String.valueOf(displayed) + 0;
        }
        if (expDisplay < 10L) {
            displayed = String.valueOf(displayed) + 0;
        }
        displayed = String.valueOf(displayed) + expDisplay;
        return displayed;
    }
    
