    public void say(final JTextPane t, final String s) {
        final StyledDocument doc = t.getStyledDocument();
        final SimpleAttributeSet keyWord = new SimpleAttributeSet();
        StyleConstants.setFontSize(keyWord, this.textSize);
        Color usedColor = this.textColor;
        if (t.getBackground().equals(Color.BLACK)) {
            usedColor = this.darkColor;
        }
        StyleConstants.setForeground(keyWord, usedColor);
        StyleConstants.setFontFamily(keyWord, "DialogInput");
        StyleConstants.setBold(keyWord, true);
        try {
            doc.insertString(doc.getLength(), s, keyWord);
        }
        catch (Exception e) {
            System.out.println(e);
        }
    }
    
    public String fixedFormat(long amount) {
        if (amount < 0L) {
            amount = 0L - amount;
        }
        int power;
        for (power = 3; amount >= 10000L; amount /= 10L, ++power) {}
        if (power == 3) {
            if (amount < 10L) {
                power = 0;
            }
            else if (amount < 100L) {
                power = 1;
            }
            else if (amount < 1000L) {
                power = 2;
            }
        }
        String format = "";
        if (power < 4 || (power - 2) % 3 == 0) {
            format = String.valueOf(format) + " ";
        }
        if (power > 2) {
            format = String.valueOf(format) + amount / 1000L;
        }
        else {
            format = String.valueOf(format) + " ";
        }
        if (power % 3 == 0 && power > 3) {
            format = String.valueOf(format) + ".";
        }
        if (power > 1) {
            format = String.valueOf(format) + amount % 1000L / 100L;
        }
        else {
            format = String.valueOf(format) + " ";
        }
        if ((power - 1) % 3 == 0 && power > 3) {
            format = String.valueOf(format) + ".";
        }
        if (power > 0) {
            format = String.valueOf(format) + amount % 100L / 10L;
        }
        else {
            format = String.valueOf(format) + " ";
        }
        if (power < 4) {
            format = String.valueOf(format) + amount % 10L;
        }
        if (power > 17) {
            format = String.valueOf(format) + "E";
        }
        else if (power > 14) {
            format = String.valueOf(format) + "P";
        }
        else if (power > 11) {
            format = String.valueOf(format) + "T";
        }
        else if (power > 8) {
            format = String.valueOf(format) + "G";
        }
        else if (power > 5) {
            format = String.valueOf(format) + "M";
        }
        else if (power > 3) {
            format = String.valueOf(format) + "k";
        }
        return format;
    }
    
